import { aliasRegex, emailRegex } from "./regex"

export const reguired = 'Это обезательное поле!'

export const email = {
   reguired,
   pattern: {
      email: emailRegex,
      message: 'Email Error!'
   }
}

export const alias = {
   reguired,
   pattern: {
      value: aliasRegex,
      message: 'Только латинские буквы и цифры'
   }
}

export const password = {
   reguired,
   pattern: {
      value: 6,
      message: 'Не менее 6 символав'
   }
}
