import { aliasRegex, emailRegex } from "./regex"

export const required = 'Это обезательное поле!'

export const email = {
   required,
   pattern: {
      email: emailRegex,
      message: 'Email Error!'
   }
}

export const alias = {
   required,
   pattern: {
      value: aliasRegex,
      message: 'Только латинские буквы и цифры'
   }
}

export const password = {
   required,
   pattern: {
      value: 6,
      message: 'Не менее 6 символов'
   }
}
