import React from "react"
import Header from "../../components/Header"
import cls from './Main.module.scss'
import { NavLink } from 'react-router-dom'
import axios from "axios"
import TextArea from "../../components/TextArea"
import { request } from "../../configs"
import Button from "../../components/Button"
import { Form } from '../../helpers/Form'
import ToDoitem from '../../components/ToDoItem'
import { useForm } from "react-hook-form"
import NoAuth from '../../components/NoAuth'
import Loading from '../../components/Loading'

const Main = () => {
   const {
      handleSubmit,
      formState,
      register,
      reset,
    } = useForm()
  
    const [isActive, setActice] = React.useState(false)
    const [data, setData] = React.useState([])
  
    const onSubmit = (data) => {
      setActice(true)
  
      const body = {
        title: data.title,
        completed: false,
      }
  
      axios.post(request('/todos.json'), body)
        .then(res => {
          reset()
        })
        .finally(() => {
          setActice(false)
        })
    }

    const deleteItem = (id) => {
      axios.delete(request(`/todo/${id}.json`))
      .then(res => console.log(res))
    }
  

    
    const putchItem = (id) => {
      const body = {
        title: prompt('Введите ToDo')
      }
      axios.patch(crud(`/${id}.json`), body)
      .then(res => console.log(res))
    }
  
    React.useEffect(() => {
      axios.get(request('/todos.json'))
      .then(res => {
        const data = Object.values(res.data)
        console.log(res)
  
        setData(data)
      })
    }, [])
    
    if (!localStorage.getItem('localId')) return (
      <NoAuth 
        link={'/signin'} 
        textContent={'Вы не авторизовались!'}
        textLink={'Войти!'}
      />
    )
    if (!data) return <h1>Loading...</h1>
    //  (<Loading />)
    return (
      <div className={cls.root}>
        <Header showLink={true} showburger={true} />
        <form className={cls.content} onSubmit={handleSubmit(onSubmit)}>
          <TextArea 
              placeholder={'New ToDo'} 
              {...register('title', Form.Options.todoTitle)}
              error={formState.errors.title}
              />
          <Button 
            type={'submit'} 
            text={'Add'}
            disabled={isActive}
            />
        </form>
         <div className={cls.container}>
          {
            data.map(({ id, title, completed }, i) => (
              <ToDoitem
              key={i}
              title={title}
              completed={completed}
              deleteItem={deleteItem}
              putchItem={putchItem}
              id={id}
              />
            ))
          }
        </div>
      </div>
    )
}
console.log(Loading)

export default Main
