import React from "react"
import cls from './TextArea.module.scss'


const TextArea = React.forwardRef(({
   placeholder,
   type='text',
   error,
   ...rest
}, ref) => {

   return (
      <div className={cls.root}>
      {
         error && <span>{error.message}</span>
      }
      <input 
         type={type}
         placeholder={placeholder}
         ref={ref}
         {...rest}
      />
      </div>
   )
})

export default TextArea
