import React from "react"
import Header from "../../components/Header"
import cls from './Main.module.scss'
import { NavLink } from 'react-router-dom'
import axios from "axios"
import TextArea from "../../components/TextArea"
import { request } from "../../configs"
import Button from "../../components/Button"
import { Form } from '../../helpers/Form'
import ToDoitem from '../../components/ToDoItem'
import { useForm } from "react-hook-form"
import NoAuth from '../../components/NoAuth'

const Main = () => {
   const {
      handleSubmit,
      formState,
      register,
      reset,
    } = useForm()
  
    const [isActive, setActice] = React.useState(false)
    const [data, setData] = React.useState([])
  
    const onSubmit = (data) => {
      setActice(true)
  
      const body = {
        title: data.title,
        completed: false,
      }
  
      axios.post(request('/todos.json'), body)
        .then(res => {
          reset()
        })
        .finally(() => {
          setActice(false)
        })
    }
  
    React.useEffect(() => {
      axios.get(request('/todos.json'))
      .then(res => {
        const data = Object.values(res.data)
        console.log(res)
  
        setData(data)
      })
    }, [])
    
    if (!localStorage.getItem('localId')) return (
      <NoAuth 
        link={'/signin'} 
        textContent={'Вы не авторизовались!'}
        textLink={'Войти!'}
      />
    )
    if (!data) return 
      <div class="spinner">
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
  </div>
    return (
      <div className={cls.root}>
        <Header showLink={true} showburger={true} />
  
        <form className={cls.content} onSubmit={handleSubmit(onSubmit)}>
          <TextArea 
              placeholder={'New ToDo'} 
              {...register('title', Form.Options.todoTitle)}
              error={formState.errors.title}
              />
          <Button 
            type={'submit'} 
            text={'Add'}
            disabled={isActive}
            />
        </form>
  
        <div className={cls.container}>
          {
            data.map(({ title, completed }) => (
              <ToDoitem
              title={title}
              completed={completed}
              />
            ))
          }
        </div>
      </div>
    )

   // return (
   //    <div className={cls.root}>
   //       <Header />
   //       <div className={cls.filter}>
   //          <h1>Все задачи</h1>
   //          <ul>
   //             <li>Покупки</li>
   //             <li>Anime</li>
   //             <li>Избранные</li>
   //          </ul>
   //       </div>
   //       <h1>Hello Main </h1>
   //    </div>
   // )
}

export default Main
