const Signin= () => {
   const [data, setData] = React.useState({
      email: '',
      password: '',
   })

   const navigate = useNavigate()

   const onChangeInputs = (value, key) => {
      setData(prev => {
         return {
            ...prev,
            [key]: value
         }
      })
   }

   const handleSubmit = (e) => {
      e.preventDefault()

      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signInUrl, body)
      .then(res => {
         const data = res.data

         localStorage.setItem('localId', data.localId)
         console.log(data)
      })

      .then(() => {
         navigate('/')
      })
      
   }

   
   const Signout = () => {
      localStorage.removeItem('localId')
   }

   if (localStorage.getItem('localId')) return
     <div>
       <h1>Вы уже авторизовались</h1>
      <button onClick={Signout}>Выйти</button>
     </div>
   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit}>
         <input 
            type="email" 
            placeholder="Email" 
            {...register('email', Form.Options.email)}
         />
         <input 
             type="password" 
             placeholder="password" 
             {...register('password', Form.Options.password)}
         />
         <button type="submit">Login</button>
      </form>
      </div>
   )
}

export default Signin
