import React from "react"
import cls from './SignIn.module.scss'
import axios from 'axios'
import { signInUrl, usersDB } from '../../../configs'
import { useForm } from 'react-hook-form'
import { Form } from "../../../helpers/Form"
import { useRef } from "react"

const Signin = () => {
   const  {
      formState,
      register,
      handleSubmit,
   } = useForm()

   const onSubmit = (data) => {
      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signInUrl, body)
      .then(res => {
         const body = {
            login: data.login,
            firstName: data.firstName,
            secondName: data.secondName,
         }

         axios.post(usersDB('/user.json'), body)
         .then(res => {
            console.log(res.data)
         })
      })
   }

   const inputRef = useRef(null);
   const onButtonClick = () => {
      inputRef.current.value = "";
    };

   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit(onSubmit)}  >

            {
               formState.errors.email && <span>{formState.errors.email.message}</span>
            }

            <input 
               type="email" 
               placeholder="Email" 
               {...register('email', Form.Options.email)}
            />

            {
               formState.errors.password && <span>{formState.errors.password.message}</span>
            }

            <input 
               ref={inputRef}
               type="password" 
               placeholder="Password"
               {...register('password', Form.Options.password)} 
            />

            <button type="submit" onClick={onButtonClick}>Login</button>
         </form>
      </div>
   )
}

export default Signin

