import React from "react"
import cls from './SignUp.module.scss'
import { signUpUrl, usersDB } from '../../../configs'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { Form } from "../../../helpers/Form"
import { useRef } from "react"
import TextArea from "../../../components/TextArea"
const Signup = () => {
   const  {
      formState,
      register,
      handleSubmit,
   } = useForm()

   const onSubmit = (data) => {
      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signUpUrl, body)
      .then(res => {
         const body = {
            login: data.login,
            firstName: data.firstName,
            secondName: data.secondName,
         }

         axios.post(usersDB('/user.json'), body)
         .then(res => {
            console.log(res.data)
         })
      })
   }

   const inputRef = useRef(null);
   const onButtonClick = () => {
      inputRef.current.value = "";
    };

   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit(onSubmit)}  >

            <TextArea 
               placeholder={"Login"}
               error={formState.errors.login}
               {...register('login', Form.Options.alias)}
            />

            <TextArea 
               placeholder={"firstName"}
            />

            <TextArea 
               placeholder={"secondName"}
            />

            <TextArea 
               placeholder={"Email"}
               error={formState.errors.email}
               {...register('email', Form.Options.email)}
            />

            <TextArea 
               placeholder={"password"}
               error={formState.errors.password}
               {...register('password', Form.Options.password)}
            />

            <button type="submit"
            //  onClick={onButtonClick}
             >Register</button>
         </form>
      </div>
   )
}

export default Signup
