import React from "react"
import cls from './SignUp.module.scss'
import { signUpUrl } from '../../../configs'
import axios from 'axios'

const Signup = () => {
   const [data, setData] = React.useState({
      login: '',
      firstName: '',
      secondName: '',
      email: '',
      password: '',
   })

   const onChangeInputs = (value, key) => {
      setData(prev => {
         return {
            ...prev,
            [key]: value
         }
      })
   }

   const handleSubmit = (e) => {
      e.preventDefault()

      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signUpUrl, body)
      .then(res => {
         const data = res.data

         console.log(data)
      })
      
   }

   return (
      <div className={cls.container}>
         <form className={cls.root}  ref={(el) => thismyForm = el}>
         <input 
            type="text" 
            placeholder="Login" 
            onChange={(e) => onChangeInputs(e.target.value, 'login')}
            value={data.login}
         />
         <input 
            type="text" 
            placeholder="First Name" 
            onChange={(e) => onChangeInputs(e.target.value, 'firstName')}
            value={data.firstName}
         />
         <input 
            type="text" 
            placeholder="Second Name" 
            onChange={(e) => onChangeInputs(e.target.value, 'secondName')}
            value={data.secondName}
         />
         <input 
            type="email" 
            placeholder="Email" 
            onChange={(e) => onChangeInputs(e.target.value, 'email')}
            value={data.email}
         />
         <input 
            type="password" 
            placeholder="Password" 
            onChange={(e) => onChangeInputs(e.target.value, 'password')}
            value={data.password}
         />
         <button type="submit" onClick={() => {
            this.myForm.reset()
         }}>Login</button>
      </form>
      </div>
   )
}

export default Signup
