import React from "react"
import cls from './SignUp.module.scss'
import { signUpUrl, usersDB } from '../../../configs'
import axios from 'axios'
import { useForm } from 'react-hook-form'

const Signup = () => {
   const  {
      formState,
      register,
      handleSubmit,
   } = useForm()

   // ref={(e) => this.myForm = e}

   const onSubmit = (data) => {
      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signUpUrl, body)
      .then(res => {
         const body = {
            login: data.login,
            firstName: data.firstName,
            secondName: data.secondName,
         }

         axios.post(usersDB('/user.json'), body)
         .then(res => {
            console.log(res.data)
         })
      })

   }

   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit}  >
         <input 
            type="text" 
            placeholder="Login" 
            // onChange={(e) => onChangeInputs(e.target.value, 'login')}
            // value={data.login}
            {...register('login', {
               required: {
                  value: true,
                  message: 'Error!',
               }
            })}
         />
         {
            formState.errors.login && <span>{formState.errors.login.message}</span>
         }
         <input 
            type="text" 
            placeholder="First Name" 
            // onChange={(e) => onChangeInputs(e.target.value, 'firstName')}
            // value={data.firstName}
            {...register('firstName', {
               required: {
                  value: true,
                  message: 'Error!',
                  }
               })}
         />
         <input 
            type="text" 
            placeholder="Second Name" 
            // onChange={(e) => onChangeInputs(e.target.value, 'secondName')}
            // value={data.secondName}
            {...register('secondName', {
               required: {
                  value: true,
                  message: 'Error!',
               }
            })}
         />
         <input 
            type="email" 
            placeholder="Email" 
            // onChange={(e) => onChangeInputs(e.target.value, 'email')}
            // value={data.email}
            {...register('email', {
               required: {
                  value: true,
                  message: 'Error!',
               }
            })}
         />
         <input 
            type="password" 
            placeholder="Password" 
            // onChange={(e) => onChangeInputs(e.target.value, 'password')}
            // value={data.password}
            {...register('password', {
               required: {
                  value: true,
                  message: 'Error!',
               }
            })}
         />
         <button type="submit" onClick={() => {
            this.handleSubmit.reset()
         }}>Login</button>
      </form>
      </div>
   )
}

export default Signup
