import React from "react"
import cls from './SignUp.module.scss'
import { signUpUrl, usersDB } from '../../../configs'
import axios from 'axios'
import { useForm } from 'react-hook-form'

const Signup = () => {
   const  {
      formState,
      register,
      handleSubmit,
   } = useForm()
   // const [data, setData] = React.useState({
   //    login: '',
   //    firstName: '',
   //    secondName: '',
   //    email: '',
   //    password: '',
   // })

   // const onChangeInputs = (value, key) => {
   //    setData(prev => {
   //       return {
   //          ...prev,
   //          [key]: value
   //       }
   //    })
   // }

   // const handleSubmit = (e) => {
   //    e.preventDefault()

   //    const body = {
   //       email: data.email,
   //       password: data.password,
   //       returnSecureToken: true,
   //    }

   //    axios.post(signUpUrl, body)
   //    .then(res => {
   //       const body = {
   //          login: data.login,
   //          firstName: data.firstName,
   //          secondName: data.secondName,
   //       }

   //       axios.post(usersDB('/user.json'), body)
   //       .then(res => {
   //          console.log(res.data)
   //       })
   //    })
      
   // }
   // // ref={(e) => this.myForm = e}

   const onSubmit = (gwe) => {
      console.log(gwe)
   }

   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit}  >
         <input 
            type="text" 
            placeholder="Login" 
            // onChange={(e) => onChangeInputs(e.target.value, 'login')}
            // value={data.login}
            {...register('login', {
               required: {
                  value: true,
                  message: 'Error!',
               }
            })}
         />
         {
            formState.errors.login && <span>{formState.errors.login.message}</span>
         }
         <input 
            type="text" 
            placeholder="First Name" 
            // onChange={(e) => onChangeInputs(e.target.value, 'firstName')}
            // value={data.firstName}
            {...register('firstName', {
               required: {
                  value: true,
                  message: 'Error!',
            }})}
         />
         <input 
            type="text" 
            placeholder="Second Name" 
            // onChange={(e) => onChangeInputs(e.target.value, 'secondName')}
            // value={data.secondName}
            {...register('secondName')}
         />
         <input 
            type="email" 
            placeholder="Email" 
            // onChange={(e) => onChangeInputs(e.target.value, 'email')}
            // value={data.email}
            {...register('email')}
         />
         <input 
            type="password" 
            placeholder="Password" 
            // onChange={(e) => onChangeInputs(e.target.value, 'password')}
            // value={data.password}
            {...register('password')}
         />
         <button type="submit" onClick={() => {
            this.handleSubmit.reset()
         }}>Login</button>
      </form>
      </div>
   )
}

export default Signup
