import React from "react"
import Header from "../../../components/Header"
import cls from './Main.module.scss'
import { NavLink } from 'react-router-dom'
import axios from "axios"
import TextArea from "../../../components/TextArea"
import { request } from "../../../configs"
import Button from "../../../components/Button"
import { Form } from '../../../helpers/Form'
import ToDoitem from '../../../components/ToDoItem'
import { useForm } from "react-hook-form"
import NoAuth from '../../../components/NoAuth'
import Loading from '../../../components/Loading'


const MainPage = () => {
  const {
    handleSubmit,
    formState,
    register,
    reset,
  } = useForm()

  const { 
    todos, 
    actions: { 
      postNewTodo,
      deleteTodo,
    }
  } = Main.Hooks.use()

  const [isActive, setIsActive] = React.useState(false)

  const onSubmit = (data) => {
    postNewTodo(data)
  }


  
  if (!localStorage.getItem('localId')) return (
    <NoAuth 
      link={'/signin'} 
      textContent={'Вы не авторизовались!'}
      textLink={'Войти!'}
    />
  )
    if (!data) return <h1>Loading...</h1>
    //  (<Loading />)
    return (
      <div className={cls.root}>
        <Header showLink={true} showburger={true} />
        <form className={cls.content} onSubmit={handleSubmit(onSubmit)}>
          <TextArea 
              placeholder={'New ToDo'} 
              {...register('title', Form.Options.todoTitle)}
              error={formState.errors.title}
              />
          <Button 
            type={'submit'} 
            text={'Add'}
            disabled={isActive}
            />
        </form>
        <div className={cls.container}>
          {
            data.map(({ id, title, completed }, i) => (
              <ToDoitem
              key={i}
              title={title}
              completed={completed}
              deleteItem={deleteItem}
              putchItem={putchItem}
              id={id}
              />
            ))
          }
        </div>
      </div>
    )
}
