   //          console.log(res.data)
   //       })
   //    })
      
   // }
   // // ref={(e) => this.myForm = e}

   return (
      <div className={cls.container}>
         <form className={cls.root} onSubmit={handleSubmit}  >
         <input 
            type="text" 
            placeholder="Login" 
            onChange={(e) => onChangeInputs(e.target.value, 'login')}
            value={data.login}
         />
         <input 
            type="text" 
            placeholder="First Name" 
            onChange={(e) => onChangeInputs(e.target.value, 'firstName')}
            value={data.firstName}
            {...register}
         />
         <input 
            type="text" 
            placeholder="Second Name" 
            onChange={(e) => onChangeInputs(e.target.value, 'secondName')}
            value={data.secondName}
         />
         <input 
            type="email" 
            placeholder="Email" 
            onChange={(e) => onChangeInputs(e.target.value, 'email')}
            value={data.email}
         />
         <input 
            type="password" 
            placeholder="Password" 
            onChange={(e) => onChangeInputs(e.target.value, 'password')}
            value={data.password}
         />
         <button type="submit" onClick={() => {
            this.handleSubmit.reset()
         }}>Login</button>
      </form>
      </div>
   )
}

export default Signup
