// import React from "react"
// import cls from './SignIn.module.scss'
// import axios from 'axios'
// import { signInUrl, usersDB } from '../../../configs'
// import { useForm } from 'react-hook-form'
// import { Form } from "../../../helpers/Form"
// import { useNavigate } from "react"
import axios from 'axios'
import React from 'react'
import { useForm } from 'react-hook-form'
import { NavLink, useNavigate } from 'react-router-dom'
import Button from '../../../components/Button'
import Header from '../../../components/Header'
import TextArea from '../../../components/TextArea'
import { signInUrl } from '../../../config'
import { Form } from '../../../helpers/Form'
import cls from './SignIn.module.scss'

const Signin= () => {
   const {
      formState,
      register,
      handleSubmit,
      res
   } = useForm()

   const [isSuccessAuth, setIsSuccesAuth] = React.useState(false)

   const navigate = useNavigate()

   const onSubmit = (data) => {
      // e.preventDefault()

      const body = {
         email: data.email,
         password: data.password,
         returnSecureToken: true,
      }

      axios.post(signInUrl, body)
      .then(res => {
         const data = res.data

         localStorage.setItem('localId', data.localId)
      })
      .then(() => {
         navigate('/')
      })
      .catch(err => {
         if(err.responce.data.error.code === 400) {
            setIsSuccesAuth(true)
         }
      })
      
   }
   return (
      <div className={cls.container}>
         <Header />
            <div className={cls.fromBox}>
               <h1 className={cls.title}>Авторизоваться</h1>
                  <form className={cls.root} onSubmit={handleSubmit}>
                     <TextArea
                        label="email" 
                        type={"Email"}
                        error={formState.errors.email}
                        {...register('email', Form.Options.email)}
                     />
                     <TextArea
                        label="password" 
                        placeholder="password" 
                        type={'password'}
                        error={formState.errors.password}
                        {...register('password', Form.Options.password)}
                     />
                     <Button type={'submit'} text={'Войти'} />

                        {
                           isSuccessAuth && (
                              <h3 className={cls.noAuth}>Вы ввелт неверный пароль или почту</h3>
                           )
                        }

                  </form>
               <p>
                  Нет аккаунта? <NavLink to={'/signup'}>Зарегистрироваться</NavLink>
               </p>
            </div>
      </div>
   )
}

export default Signin
