import React from "react"
import cls from './Navbar.module.scss'
import { NavLink } from 'react-router-dom'
import { useState, useEffect } from "react"

const Header = () => {

   const [navbars, setNavbars] = useState(false)

   const changeBacground = () => {
     if(window.scrollY > 30) {
       setNavbars(true)
     } else {
       setNavbars(false)
     }
   }
 
   window.addEventListener('scroll', changeBacground)

   return (
      <header className={navbars ? 'header active' : 'header'} 
      // className={navbar ? 'd-flex justify-between align-center header active' : 'd-flex justify-between align-center header'}
      >
         <h1>
            ToDo
         </h1>
         <p>
            <NavLink to={'/auth/signup'}>
               No Account
            </NavLink>
         </p>
      </header>
   )
}

export default Header
